class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int l = 0, r = 0, n = colors.size(), ans = 0;
        while (r < n + k - 1)
        {
            if (l == r) r++;
            while (r < n + k - 1 && r - l < k && colors[r % n] != colors[(r - 1) % n])
                r++;
            if (r - l == k) ans++, l++;
            else l = r;
        }
        return ans;
    }
};
