class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& arr, int k) {
        int n = arr.size();
        int ans = 0;

        if (k == 1) {
            return n;
        }

        int curr = 1;
        for (int i = 1; i < n + k - 1; i++) {
            int x = i % n;
            int prev = (i - 1) % n;

            if (arr[x] != arr[prev]) {
                curr++;
            } else {
                curr = 1;
            }

            if (curr >= k) {
                ans++;
            }
        }

        return ans;
    }
};
