class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        if (n < k) return 0; 
        int count = 0;
        int alternating_count = 0;

        for (int i = 0; i < k-1; ++i) {
            if (colors[i] != colors[(i + 1) % n]) {
                alternating_count++;
            }
        }

        if (alternating_count == k-1) {
            count++;
        }
        //cout<<alternating_count<<endl;
        for (int i = 1; i < n; ++i) {
            if (colors[(i - 1) % n] != colors[(i - 1 + 1) % n]) {
                alternating_count--;
            }

            if (colors[(i + k - 2) % n] != colors[(i + k - 1) % n]) {
                alternating_count++;
            }
            //cout<<alternating_count<<endl;
            if (alternating_count == k-1) {
                count++;
            }
        }

        return count;
    }
};
