class Solution {
    public int numberOfAlternatingGroups(int[] C, int k) {
         int[] extendedC = new int[C.length + k - 1];

        // Copy elements from C to extendedC to simulate circular array
        System.arraycopy(C, 0, extendedC, 0, C.length);
        System.arraycopy(C, 0, extendedC, C.length, k - 1);

        int R = 0, N = 1;

        // Traverse through the extended array to count alternating groups
        for (int i = 1; i < extendedC.length; ++i) {
            if (extendedC[i] != extendedC[i - 1]) {
                N++;
            } else {
                N = 1;
            }
            if (N >= k) {
                R++;
            }
        }

        return R;
    }
}
