class Solution {
public:
    int secret(vector<int>&nc,int k){
        int n=nc.size();
        int cnt=1;
        int maxans=0;
        for(int i=0;i<n-1;i++){
            if(nc[i]!=nc[i+1]){
                cnt++;
            }
            else{
                maxans+=max(0,cnt+1-k);
                cnt=1;
            }
        }
        maxans+=max(0,cnt+1-k);
        return maxans;
    }
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int>nc(colors);
        for(int i=0;i<k-1;i++){
            nc.push_back(colors[i]);
        }
        return secret(nc,k);
    }
};
