class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        for(int i = 0; i < n; i++) colors.push_back(colors[i]);
        vector<int> dp(2*n); dp.back() = 1;
        for(int i = 2*n-2; i >= 0; i--){
            if(colors[i] != colors[i+1]) dp[i] = dp[i+1]+1;
            else dp[i] = 1;
        }
        int cnt = 0;
        for(int i = 0; i < n; i++) if(dp[i] >= k){
            cnt++;
        }
        return cnt;
    }
};
