class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        for(int i = 0;i<n;i++)
        {
            colors.push_back(colors[i]);
        }
        int s = 1;
        int e = 0;
        int ans = 0;
        for(auto it : colors)
        {
            cout<<it<<" ";
        }
        cout<<endl;
        while(e<n && s-k<n)
        {
            if(colors[s] != colors[s-1])
            {
                s++;
            }else{
                e = s;
                s++;
            }
            int cnt = 0;
            if(s-e >= k)
            {
                cnt++;
                e++;
            }
            ans += cnt;
        }
        return ans;
    }
};
