class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        for(int i=0;i<n;++i)
        {
            colors.push_back(colors[i]);
        }
        int n2=n*2;
        int ans=0;
        for(int i=0;i<n;)
        {
            int j=i+1;
            for(;j<n2 && colors[j-1]!=colors[j];++j);
            if(j-i>=k) ans+=min(j-k,n-1)-i+1;
            i=j;
        }
        return ans;
    }
};
