class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int ans = 0;
        int n = colors.size();

        for(int i = 0;i < n ; i++){
            colors.push_back(colors[i]);
        }

        vector<int> dp(2*n);

        for(int i = 2*n-2; i>=0; i--){
            if(colors[i] != colors[i+1])    dp[i] = dp[i+1] + 1;
            else dp[i] = 0;
        }

        // for(int i = 0;i < 2*n ; i++){
        //     cout << dp[i] << " ";
        // }
        // cout << endl;

        for(int i = 0;i < n ; i++){
            if(dp[i]+1 >= k)   ans++;
        }

        return ans;
    }
};
