class Solution {
public:
	int numberOfAlternatingGroups(vector<int>& colors,int k) {
		vector<int> a;
		int n = colors.size();
		for(int i = n - k + 1;i < n;i++){
			a.push_back(colors[i]);
		}
		for(auto x : colors) a.push_back(x);
		int ans = 0;
		vector<int> vis(a.size());
		for(int i = 1;i < a.size() - 1;i++){
			if(a[i] != a[i - 1] && a[i] != a[i + 1]){
				vis[i] = 1;
			}
		}
		for(int i = 1;i < vis.size();i++){
			vis[i] += vis[i - 1];
		}
		for(int i = 0;i < vis.size() - k + 1;i++){
			//i~i+k-1
			//查看i+1~i+k-2之间是否全是1
			if(vis[i + k - 2] - vis[i] == i + k - 1 - i + 1 - 2){
				ans++;	
			}
		}
		return ans;
	}
};
