class Solution {
     public int numberOfAlternatingGroups(int[] c, int k) {
        int n = c.length;
        int[] nc = new int[n + k - 1];
        for (int i = 0; i < n; ++i) nc[i] = c[i];
        for (int i = 0; i < k - 1; ++i) nc[n + i] = c[i];
        int ans = 0 , num = 1;
        for (int i = 1; i < nc.length; ++i) {
            if (nc[i] != nc[i - 1]) num++;
            else num = 1;
            if (num>=k) ans++;
        }
        return ans;
    }
}
