class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        for(int i=0;i<n;i++)
        {
            colors.push_back(colors[i]);
        }
        multiset<int> conzero;
        multiset<int> conones;
        for(int i=0;i<n+k-1;i++)
        {
            if(colors[i]==colors[i+1])
            {
                if(colors[i]==0)
                    conzero.insert(i+1);
                else
                {
                    conones.insert(i+1);
                }
            }
        }
        int ans=0;
        for(int i=0;i<n;i++)
        {
            auto it=conzero.upper_bound(i);
            if(it==conzero.end() || (*it>=(i+k)))
            {
                it=conones.upper_bound(i);
                if(it==conones.end() || (*it>=(i+k)))
                {
                    ans+=1;
                }
            }
        }
        return ans;
    }
};
