class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:

        if len(colors)<k:
            return 0
        cnt =0
        ans =0
        l = len(colors)
        i =0
        j =0

        while j<l+k-2:
            if colors[j%l] != colors[(j+1)%l]:
                cnt+=1
            else:
                i =j+1
                cnt=0

            if cnt+1>=k:
                ans+=1
            j+=1

        return ans
