class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        s1 ,s2 = '' , ''
        i1 = 1 
        i2 = 0
        for i in range(k):
            s1 +=(str(i1))
            i1 = 1 - i1

            s2 +=(str(i2))
            i2 = 1 - i2
        ans = ''
        for i in colors:
            ans += str(i)
        ans *=2
        res = 0

        for i in range(len(colors)):
            if(ans[i : i + k] == s1 or ans[i : i+k] == s2):
                res += 1
        return res
