class Solution {
public:
  int numberOfAlternatingGroups(vector<int>& colors, int k) {
    int n = colors.size();
    vector<int> dp(n, 1);
    for(int i = 1; i < n; i++) {
      if(colors[i] != colors[i - 1]) {
        dp[i] = 1 + dp[i - 1];
      }
    }

    int ans = 0;

    for(int i = 0; i < n; i++) {

      if(i + k - 1 < n) {

        ans += (dp[i + k - 1] >= k);

      }else {

        int j = (i + k - 1) % n;

        ans += (colors[n - 1] != colors[0] && dp[n - 1] >= n - i &&  dp[j] == j + 1);

      }
    }
    return ans;

  }
};
