#include <vector>
using namespace std;

class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        return (k == 1) ? colors.size() : countGroups(colors, k);
    }

private:
    int countGroups(const vector<int>& colors, int k) {
        int n = colors.size(), count = 0, length = 1, i = 1;
        int c, prev;
        while (i < n + k - 1) {
            c = i % n;
            prev = (i - 1) % n;

            length = (colors[c] != colors[prev]) ? length + 1 : 1;
            count += (length >= k) ? 1 : 0;

            ++i;
        }

        return count;
    }
};
