class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int ans=0;
        if(k==1)return n;
        int rand = 1;
        int ind = 1;
        while(ind<n+k-1){
            int prev = ind-1;
            int cur = ind;
            if(cur>=n)cur-=n;
            if(prev>=n)prev-=n;
            if(colors[cur]!=colors[prev]){
                rand++;
            }else{
                rand=1;
            }
            if(rand>=k)ans++;
            ind++;
        }
        return ans;
    }
};
