class Solution {
public: 
    int numberOfAlternatingGroups(vector<int>& a, int k) { 
        int n = a.size(); 
        int cnt = 0; 
        if(k==1)return n;

        int temp=1;
        int i=1;
        while(i<n+k-1){
            int cur=i;
            int prev=i-1;
            if(cur>=n)cur-=n;
            if(prev>=n)prev-=n;
            if(a[cur]!=a[prev])temp++;
            else temp=1;
            if(temp>=k)cnt++;
            i++;
        }
         return cnt; 
    } 
};
