class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        // k/2 k-k/2
        for(int i=0;i<k-1;++i){
            colors.push_back(colors[i]);
        }
        vector<int>p0(colors.size()),p1(colors.size());
        p0[0] = colors[0];
        p1[0] = 0;
        for(int i=1;i<colors.size();++i){
            if(i%2==0){
                p0[i]=colors[i];
            }else{
                p1[i]=colors[i];
            }
            p0[i]+=p0[i-1];
            p1[i]+=p1[i-1];
        }
        // for(auto x : p0){
        //     cout<<x<<" ";
        // }
        // cout<<endl;
        // for(auto x : p1){
        //     cout<<x<<" ";
        // }
        // cout<<endl;
        int oce=k/2;
        int ece=k/2 + (k%2);
        int oco=k/2 + (k%2);
        int eco=k/2;
        int ct=0;
        for(int i=k-1;i<colors.size();++i){
            int es = p0[i] - p0[i-k+1] + (((i-k+1)%2==0)?colors[i-k+1]:0);
            int os = p1[i] - p1[i-k+1] + (((i-k+1)%2==0)?0:colors[i-k+1]);
            //cout<<"i "<<i<<" es "<<es<<" os "<<os<<endl;
            if(es==0){
                if(i%2==0){
                    ct += os == oce;
                } else {
                    ct += os == oco;
                }
            } else if (os == 0){
                if(i%2==0){
                    ct += es == ece;
                } else {
                    ct += es == eco;
                }
            }
        }
        return ct;
    }
};
