class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:

        colors += colors
        ans = 0
        streak = 0
        p = -1
        for i in range(len(colors) // 2 +  k):
            if colors[i] != p:
                streak += 1
            else:
                ans += streak - k + 1 if streak - k >= 0 else 0
                streak = 1
            p = colors[i]

        if streak != 1:
            ans += streak - k if streak - k >= 0 else 0

        return ans
