class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        if k == 1:
            return n

        res = set()
        alternating_length = 0

        for i in range(2*n):
            if colors[i % n] != colors[(i-1) % n]:
                alternating_length += 1
                if alternating_length >= k:
                    res.add(i%n)
            else:
                alternating_length = 1

        return len(res)
