class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {

        int n = colors.length;
        int n3 = n * 3;

        int[] nc = new int[n3];
        int[] cl = new int[n3];

        System.arraycopy(colors, 0, nc, 0, n);
        System.arraycopy(colors, 0, nc, n, n);
        System.arraycopy(colors, 0, nc, n * 2, n);

        for (int i = 0; i < n3; i++) {
            int j = i + 1;
            while (j < n3 && nc[j] != nc[j - 1]) {
                cl[j] = cl[j - 1] + 1;
                j++;
            }
            i = j - 1;
        }

        int result = 0;
        for (int i = n; i < n * 2; i++) {
            if (cl[i] + 1 >= k) result++;
        }

        return result;

    }
}
