class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        bool check = false;
        int cnt=1;
        int ans=0;
        int n = colors.size();
        for(int i=0;i<colors.size()+k-2;i++){
            if(colors[i%n] == colors[(i+1)%n]){
                cnt = 1;
                check = false;

            }
            else{
                if(check == true){
                    ans++;
                }
                else{
                    cnt++;
                    if(cnt>=k){
                        ans++;
                        check = true;
                    }
                }
            }

        }
        return ans;
    }
};
