class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& arr, int k) {
        int n = arr.size();
        int alternatingGroupCount = 0;
        int startIndex = 0, endIndex = 1;

        while (endIndex < n + k && startIndex < n) {
            int currentEndIndex = endIndex;
            int circularEndIndex = currentEndIndex % n;

            while (arr[circularEndIndex] != arr[(circularEndIndex - 1 + n) % n] && (currentEndIndex < (n + k - 1))) {
                currentEndIndex++;
                circularEndIndex = currentEndIndex % n;
            }

            alternatingGroupCount += max(currentEndIndex - startIndex - k + 1, 0);
            startIndex = currentEndIndex;
            endIndex = startIndex + 1;
        }

        return alternatingGroupCount;
    }
};
