template<class T> std::ostream& operator<<(std::ostream& os, const std::vector<T>& a) { for (const T& x : a) os << x << ' '; return os; }

class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        // std::cout << "hey" << '\n';
        int n = colors.size();
        for (int i = 0; i < k-1; i++) {
            colors.push_back(colors[i]);
        }
        // std::cout << "Yes" << '\n';
        int m = colors.size();
        std::vector<int> diff(m-1);
        for (int i = 0; i < m-1; i++) {
            diff[i] = (colors[i] != colors[i+1]);
        }
        // std::cout << "colors: " << colors << '\n';
        // std::cout << "Yes" << '\n';
        std::vector<int> sum(m);
        for (int i = 0; i < m-1; i++) {
            sum[i+1] = sum[i]+diff[i];
        }
        // std::cout << "Yes" << '\n';
        int ans = 0;
        for (int i = 0; i < n; i++) {
            int l = i, r = i+k-1;
            // [l, r-1]
            if (sum[r-1+1]-sum[l-1+1] == r-1-l+1) ans++;
        }
        return ans;
    }
};
