class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {

        vector<int> temp=c;

        for(int i=0; i<c.size(); i++)
        {
            temp.push_back(c[i]);
        }

        vector<int> pre(temp.size());
        pre[0]=1;

        for(int i=1; i<temp.size(); i++)
        {
            if(temp[i]!=temp[i-1])
            {
                pre[i]=pre[i-1];
            }
            else
            {
                pre[i]=pre[i-1]+1;
            }
        }

        int l=0, r=k-1, cnt=0;

        while(l<c.size())
        {
            if(pre[l]==pre[r])
            {
                cnt++;
            }

            l++;
            r++;
        }

        return cnt;
    }
};
