class Solution {
public:

    vector<int> prefix_function(string s){
        int n=s.length();
        vector<int> lps(n,0);

        for(int i=1;i<n;i++){
            int prev_idx=lps[i-1];

            while(prev_idx>0 && s[i]!=s[prev_idx]){
                prev_idx=lps[prev_idx-1];
            }

            lps[i] = prev_idx + (s[i]==s[prev_idx]);
        }
        return lps;
    }

    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        if(k==1 || n==0) return n;
        string str="";
        for(int i=0;i<n;i++){
            str+='0'+colors[i];
        }
        string temp="";
        temp+=str;
        temp+=str;
        // cout<<"String:- ";
        // cout<<temp<<endl;
        string p1="";
        int i=0;
        int curr=0;
        while(i<k){
            p1+='0'+curr;
            curr=1-curr;
            i++;
        }
        string p2="";
        i=0;
        curr=1;
        while(i<k){
            p2+='0'+curr;
            curr=1-curr;
            i++;
        }
        string s1=p1+'#'+temp;
        string s2=p2+'#'+temp;

        // cout<<"Pattern1:- "<<p1<<endl;
        // cout<<"Pattern2:- "<<p2<<endl;

        vector<int> v1=prefix_function(s1);
        vector<int> v2=prefix_function(s2);
        int cnt=0;
        for(int i=0;i<n+2*k;i++){
            if(v1[i]==k) cnt++;
            if(v2[i]==k) cnt++;
        }
        return cnt;
    }
};
