#include <vector>
using namespace std;

class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int N = colors.size();
        int count = 0;

        if (k == 1) {
            return N;
        }

        int len = 1;
        int i = 1;
        while (i < N + k - 1) {
            int cur = i;
            int prev = i - 1;

            if (cur >= N) {
                cur -= N;
            }
            if (prev >= N) {
                prev -= N;
            }

            if (colors[cur] != colors[prev]) {
                ++len;
            } else {
                len = 1;
            }

            if (len >= k) {
                ++count;
            }

            ++i;
        }

        return count;
    }
};
