#define ll long long
#define ll long long
#define pb push_back
class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        vector<int>v;
        int n=c.size();
        v.pb(100);
        for(int x:c){v.pb(x);}
        for(int x:c){v.pb(x);}
        n=(2*n);
        int dp[n+1];
        dp[n]=1;
        for(int i=n-1;i>=1;i--)
        {
            if(v[i]!=v[i+1]){dp[i]=1+dp[i+1];}
            else{dp[i]=1;}
        }
        int ans=0;
        for(int i=1;i<=(n/2);i++){
            if(dp[i]>=k){ans++;}
        }
        return ans;
    }
};
