class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k) {
        int n = v.size();
        if (k == 1) {
            return n;
        }
        int cnt = 0;
        int len = 1;

        for (int i = 1; i < n+k-1;i++) 
        {
            int curr = i%n;
            int prev = (i-1)%n;            
            if(v[curr]!=v[prev])
            {
                len++;
            }
            else
            {
                len =1;
            }
            if (len>=k) 
            {
                cnt++;
            }
        }
        return cnt;
    }
};
