class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int l = 0;
        int r = 1;
        int n = colors.length;
        int res = 0;

        while (r < n + k && l < n) {
            int temp1 = r;
            int temp2 = temp1 % n;
            while (colors[temp2] != colors[(temp2 - 1 + n) % n] && temp1 < n + k - 1) {
                temp1++;
                temp2 = temp1 % n;
            }
            res += Math.max(temp1 - l - k + 1, 0);
            l = temp1;
            r = l + 1;
        }

        return res;
    }
}
