class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
                vector<int> tmp = colors;
        for(int i = 0;i < colors.size();++i){
            tmp.push_back(colors[i]);
        }
        int end = colors.size() + k - 1;
        int l = 0,r = 0;
        int cnt = 0;
        int ans = 0;
        //cout << end << '\n';
        while(r < end){
            if(r >= k - 1){
                ans += cnt == 0;
                if(tmp[l + 1] == tmp[l])cnt--;
                l++;
            }
            if(tmp[r+1] == tmp[r])cnt++;
            r++;
        }
        return ans;
    }
};
