class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int n = c.size();
        if (k > n) return 0;


        int alt1 = 0, alt2 = 0;

        for (int i = 0; i < k; i++) {
            if (c[i % n] == (i % 2)) alt1++;
            if (c[i % n] == ((i + 1) % 2)) alt2++;
        }

        int cnt = 0;
        if (alt1 == k || alt2 == k) cnt++;
        for(int i = k; i < n + k - 1; i++) {
            if (c[(i - k) % n] == ((i - k) % 2)) alt1--;
            if (c[(i - k) % n] == ((i - k + 1) % 2)) alt2--;

            if (c[i % n] == (i % 2)) alt1++;
            if (c[i % n] == ((i + 1) % 2)) alt2++;

            if (alt1 == k || alt2 == k) cnt++;
        }

        return cnt;
    }

};
