class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        curr=0
        n=len(colors)
        for i in range(k-1):
            if colors[i]!=colors[i+1]:
                curr+=1

        ans=0
        if curr==k-1:
            ans+=1

        # print(curr)

        for i in range(1,n):
            if colors[i]!=colors[i-1]:
                curr-=1
            if colors[(i-1+k+n)%n]!=colors[(i-2+k+n)%n]:
                curr+=1
            # print(curr)
            if curr==k-1:
                ans+=1

        return ans
