class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& nums, int k) {
        int n = nums.size();
        if (k > n) return 0;

        int count = 0;
        int currcnt = 1;

        for (int i = 1; i < n; ++i) {
            if (nums[i] != nums[i - 1]) {
                currcnt++;
            } else {
                currcnt = 1;
            }
            if (currcnt == k) {
                count++;
                currcnt--;  
            }
        }
        for (int i = 0; i < k - 1; ++i) {
            if (nums[i] != nums[(n + i - 1) % n]) {
                currcnt++;
            } else {
                currcnt = 1;
            }
            if (currcnt == k) {
                count++;
                currcnt--;
            }
        }

        return count;
    }
};
