class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int s = colors.size();
        for(int i = 0; i < k-1 ; i++){
            colors.push_back(colors[i]);
        }
        int count = 0 , res = 0 , N = colors.size();
        for(int i = 0; i < colors.size()-1 ; i++){
            if(colors[i] != colors[i+1]){
                count++;
            }
            else{
                count = 0;
            }
            if(count >= k-1){
                res++;
            } 
        }
        return res;
    }
};
