class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        window = []
        res = 0

        for i in range(k-3):
            if colors[i] not in {colors[i-1], colors[i+1]}:
                window.append(i)
            else:
                window = []

        for i in range(k-3, len(colors)-1):
            if colors[i] not in {colors[i-1], colors[i+1]}:
                window.append(i)
            else:
                window = []
            res += len(window) >= k - 2

        end = len(colors)-1
        if colors[end] not in {colors[0], colors[end-1]}:
            window.append(end)
            res += len(window) >= k - 2

        for i in range(k-3):
            if colors[i] not in {colors[i-1], colors[i+1]}:
                window.append(i)
            else:
                window = []
            res += len(window) >= k - 2

        return res
