class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& col, int k) {
        int n=col.size();
        if(n<k)
            return 0;
        int it = 0;
        for(int i=0;i<k-1;i++){
            if(col[i]==col[i+1])
                it++;
        }
        int ans=0;
        if(it==0)
            ans++;
        for(int i=0,j=k;i<n-1;i++,j++){
            if(col[i]==col[(i+1)%n])
                it--;
            if(col[j%n]==col[(j-1)%n])
                it++;
            if(it==0)
                ans++;
        }
        return ans;
    }
};
