class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        int x=0;
        while(x<k-1){
            colors.push_back(colors[x]);
            x++;
        }
        int ans=0;
        int ct=1;
        for(int i=1;i<colors.size();i++){
            if(colors[i]!=colors[i-1]){
                ct++;
            }
            else{
                ct=1;
                }
            if(ct==k){
                ans++;
                ct--;
            }
        }
        return ans;
    }
};
