class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        colors.insert(colors.end(), colors.begin(), colors.begin() + k);
        int n = colors.size();
        int ans = 0;
        unordered_map<string, int> mpp;
        vector<int> pd(n + 1, 0);
        for (int i = 1; i < n; ++i) pd[i] += pd[i - 1] + abs(colors[i]-colors[i-1]);
        k --;
        for (int i = k+1; i < n; ++i) {
            if (pd[i] - pd[i - k] == k) ans++;
        }
        return ans;
    }   

};
