class Solution {
public:
    void lpsarray(string& pat, vector<int>& lps, int m)
    {
        int l=0;
        lps[0]=0;

        int i=1;
        while(i<m)
        {
            if(pat[i]==pat[l])
            {
                l++;
                lps[i]=l;
                i++;
            }
            else
            {
                if(l!=0)
                {
                    l=lps[l-1];
                }
                else
                {
                    lps[i]=0;
                    i++;
                }
            }
        }
        // for(int x:lps)
        //     cout<<x<<" ";
    }

    int kmp(string& text, string& pat)
    {
        int n=text.size(), m=pat.size();
        vector<int> lps(m);
        lpsarray(pat,lps,m);

        int i=0,j=0;
        int count=0;
        while((n-i)>=(m-j))
        {
            if(text[i]==pat[j])
            {
                j++;i++;
            }

            if(j==m)
            {
                count++;
                j=lps[j-1];
            }
            else if(i<n && text[i]!=pat[j])
            {
                if(j!=0)    j=lps[j-1];
                else    i=i+1;
            }
        }

        return count;
    }
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        string s="";
        for(int i=0;i<n+k-1;i++)
            s+=char(colors[i%n]+'0');

        string patt1="",patt2="";
        int t=0;
        for(int i=0;i<k;i++)
        {
            patt1+=char(t+'0');
            t=!t;
            patt2+=char(t+'0');
        }

        // cout<<s<<endl;
        // cout<<patt1<<endl;
        // cout<<patt2<<endl;

        int res=kmp(s,patt1)+kmp(s,patt2);


        return res;
    }
};
