class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        int i=0;
        int j=0;
        int cnt=0;
        if(k>n)return 0;
        while(i<n){
            if(j-i+1<k){
                if(colors[(j+1)%n]!=colors[j%n]){
                    j++;
                }else{
                    j++;
                    i=j;
                }
            }
            else if(j-i+1==k){
                cnt++;
                if(colors[(j+1)%n]!=colors[j%n]){
                    i++;
                    j++;
                }else{
                    j++;
                    i=j;
                }
            }
        }
        return cnt;
    }
};
