class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        c.insert(c.end(),c.begin(),c.begin() + k);
      int num = c.size();
      int c1 = 0;
      unordered_map<string,int> ans;
      vector<int> pre(num + 1,0);

      for(int i = 1;i < num;i++){
        pre[i] += pre[i - 1] + abs(c[i] - c[i - 1]);
      }
      k--;
      for(int i = k + 1;i < num;i++){
        if(pre[i] - pre[i - k] == k){
          c1++;
        }
      }
      return c1;
    }
};
