class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n=a.size();
        int res=0,cnt=0;
        for(int i=0;i+1<n+k-1;i++){
            cnt+=(a[i%n]!=a[(i+1)%n]);
            if(a[i%n]==a[(i+1)%n]){
                res+=max(0,cnt+1-k+1);
                cnt=0;
            }
        }
        res+=max(0,cnt+1-k+1);
        return res;
    }
};
