class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& col, int k) {
        col.insert(col.end(), col.begin(), col.begin() + k - 1);
        int ans = 0;
        int cnt = 1;
        int i = 1;
        while(i<col.size()){
            if(col[i]!=col[i-1]){
                cnt++;
            }else{
                cnt = 1;
            }
            if(cnt>=k){
                ans++;
            }
            i++;
        }
        return ans;
    }
};
