class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
    if (k > n) return 0; 

    int count = 0;
    int ans = 1; 

    for (int i = 1; i < n + k - 1; ++i) {
        if (colors[i % n] != colors[(i - 1) % n]) {
            ++ans;
        } else {
            ans = 1;
        }

        if (ans == k) {
            ++count;
            ans = k - 1; 
        }
    }

    return count;
    }
};
