class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int count = 0;
    int i = 0;
    int j = 1;
    int n = colors.size();

    while (j < n + k && i < n) {
        int tempJ21 = j;
        int tempEnd11 = tempJ21 % n;


        while (colors[tempEnd11] != colors[(tempEnd11 - 1 + n) % n] && tempJ21 < n + k - 1) {
            tempJ21++;
            tempEnd11 = tempJ21 % n;
        }


        count += max(tempJ21 - i - k + 1, 0);
        i = tempJ21;
        j = i + 1;
    }

    return count;

    }
};
