#include<bits/stdc++.h>
using namespace std;

#define ll long long int 
#define f first
#define s second




class Solution {
public:


 ll solve(vector<int>& col, int k){

 ll x=col.size();
 ll n_fnded=0;
 ll i=0;
 ll j=1;
  while(j<x+k&& i<x){

      ll tempj=j;
      ll temp_last=tempj%x;
      while(col[temp_last]!=col[(temp_last-1+x)%x]&&tempj<(x+k-1)){
        tempj+=1;
        temp_last=tempj%x;
      }

        n_fnded+=1ll*max(1ll*0,tempj-i-k+1);
        i=tempj;
        j=i+1;

  }

return n_fnded;

 }



    int numberOfAlternatingGroups(vector<int>& colors, int k) {


        ll ans=solve(colors,k);

        return ans;



    }
};
