class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& co, int k) {
        int n = co.size();
        for(int i=0;i<k-1;i++){
            co.push_back(co[i]);
        }
        n = co.size();
        int i=0,j=1;
        int cnt = 0;
        while(j<n){
            if(co[j] == co[j-1]) i = j;
            if(j-i+1 == k){
                cnt++;
                i++;
                j++;
            }
            else{
                j++;
            }
        }
        return cnt;
    }
};
