class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        N = len(colors)
        for i in range(N):
            colors.append(colors[i])
        ans = 0
        a,b = [0]*(2*N-1),[0]*(2*N)
        for i in range(2*N-1):
            if colors[i]==colors[i+1]:
                a[i] = 1
            else:
                a[i] = 0
            b[i+1] = b[i] + a[i]
        for i in range(k-1, k-1+N):
            if b[i]==b[i-(k-1)]:
                ans += 1
        return ans
